			<#assign locationExists = (data.locationName?? &&
			data.locationName != "")>
			<#assign departmentExists = (data.departmentName?? && data.departmentName
			!= "")>
			<#assign classExists = (data.className?? && data.className != "")>
			<#if locationExists || departmentExists || classExists>



					<#if locationExists>
					${templateLabel.location}: 
					${data.locationName}
					<#elseif departmentExists>
					${templateLabel.department}: 
					${data.departmentName}
					<#else>
					${templateLabel.class}: 
					${data.className}
					</#if>


					<#if locationExists>
					<#if departmentExists>
					${templateLabel.department}: 
					${data.departmentName}
					<#elseif classExists>
					${templateLabel.class}: 
					${data.className}
					</#if>
					<#else>
					<#if departmentExists && classExists>
					${templateLabel.class}: 
					${data.className}
					</#if>
					</#if>


					<#if locationExists && departmentExists && classExists>
					${templateLabel.class}: 
					${data.className}
					</#if>


			</#if>









				${templateLabel.Items}
				${templateLabel.Lines}
				${templateLabel.CurrentPeriod}
				${templateLabel.PriorPeriod}

			<#list lineItems.desc as line>


				<#if line.center == 'False'>

					<#if line.padding == 'True'>

					</#if>
					<#if line.isBold == 'True'>
					${line.label}

				<#elseif line.isBold == 'False'>
				${line.label}

	</#if>
	<#elseif line.center == 'True'>

		<#if line.padding == 'True'>

		</#if>
		<#if line.isBold == 'True'>
		${line.label}

	<#elseif line.isBold == 'False'>
	${line.label}

	</#if>
	</#if>
	${line_index?number + 1}
	${data.repDataCurrent[line_index?number].amount}
	${data.repDataPrior[line_index?number].amount}


</#list>
